mapper.update:
Update Book
title = #{title}, ISBN = #{ISBN}, publisher_id = #{publisherId}, genre_id = #{genreId}, price = #{price}, pub_date = #{pubDate}, author = #{author}, pages = #{pages}, size = #{size}
where ISBN = #{ISBN}

mapper.select:
select book_id
from book
where publisher_id = #{publisherId} AND ISBN = #{ISBN}
